import "../styles/admin.css";
import { useState } from "react";

const Admin = () => {
  const [coupon, setCoupon] = useState({});

  const codeChange = (e) => {
    console.log("Code Changed", e.target.value);

    let copy = coupon;
    copy.code = e.target.value;
    setCoupon(copy);
  };

  const saveCoupon = () => {
    console.log("Button works");
  };
  return (
    <div className="admin">
      <h1>Admin Page</h1>
      <div className="admin-container">
        <div className="products">
          <h3>Register New Products</h3>
        </div>
        <div className="coupons">
          <h3>Register A New Coupon</h3>
          <div className="form">
            <div>
              <label className="form-label">Code</label>
              <input
                type="text"
                className="form-control"
                onChange={codeChange}
              ></input>
            </div>
            <div>
              <label className="form-label">Discount</label>
              <input type="text" className="form-control"></input>
            </div>
            <button className="btn btn-dark" onClick={saveCoupon}>
              Register
            </button>
          </div>
          Lorem ipsum dolor sit amet consectetur adipisicing elit. Dolores nisi
          voluptate quibusdam impedit. Molestiae a consectetur iste tenetur, aut
          nam laborum delectus distinctio vel, doloremque, provident enim
          maiores assumenda laudantium!
        </div>
      </div>
    </div>
  );
};

export default Admin;
