import "../styles/hero.css";

const Hero = () => {
  var banner = [
    ["index.html", "strawberries.jpg"],
    ["destination2.com", "potatoes.jpg"],
    ["destination3.com", "honey-jars.jpg"],
    ["destination4.com", "Ears-corn.jpg"],
  ];
  function shuffle(a) {
    var j, x, i;
    for (i = a.length - 1; i > 0; i--) {
      j = Math.floor(Math.random() * (i + 1));
      x = a[i];
      a[i] = a[j];
      a[j] = x;
    }
    return a;
  }
  shuffle(banner);

  return (
    <div id="ad-container">
      <a href={banner[0][0]} target="_blank" rel="nofollow">
        <img
          src={"./img/" + banner[0][1]}
          alt="Banner Ad"
        ></img>
      </a>
    </div>
  );
};

export default Hero;
