import "../styles/hero.css";

const Hero = () => {
  var banner = [
    ["destination1.com", ""],
    ["destination2.com", "placeholder.com/image2.jpg"],
    ["destination3.com", "placeholder.com/image3.jpg"],
    ["destination4.com", "placeholder.com/image4.jpg"],
  ];
  function shuffle(a) {
    var j, x, i;
    for (i = a.length - 1; i > 0; i--) {
      j = Math.floor(Math.random() * (i + 1));
      x = a[i];
      a[i] = a[j];
      a[j] = x;
    }
    return a;
  }
  shuffle(banner);
  document.getElementById("ad-container").innerHTML =
    '<a href="' +
    banner[0][0] +
    '" target="_blank" rel="nofollow"><img src="' +
    banner[0][1] +
    '" height="250" width="300" alt="300x250 Banner Ad" /></a>';

  return <div id="ad-container"></div>;
};

export default Hero;
